using System.Net.Http;
using System.Threading.Tasks;
using dotAPNS;
using System.Windows.Forms;
using System.Diagnostics;
using System;

public static class Snippet
{
	public static System.String sendNotificationToAppleAPN(System.String title, System.String message, System.String deviceID, System.String teamID, System.String certificateFilePath, System.String keyID, System.String bundleID)
	{		
		string apiResult = "";
		var options = new ApnsJwtOptions()
            {
                BundleId = bundleID,                
                CertFilePath = certificateFilePath,
                KeyId = keyID,
                TeamId = teamID
            };

            try
            {
                ApnsClient apns = ApnsClient.CreateUsingJwt(new HttpClient(new WinHttpHandler()), options);
                var push = new ApplePush(ApplePushType.Alert)
                   .AddAlert(title, message)                   
                   .AddToken(deviceID);

                var response = apns.SendAsync(push).Result;

				if(response.IsSuccessful)
					apiResult = "Notification sent";
				else
				{
					MessageBox.Show("Notification failed for device " + deviceID + " with reason "+ response.Reason);
					apiResult = "Notification failed for device " + deviceID + " with reason "+ response.Reason;
				}
            }           

            catch (Exception e)
            {                
				MessageBox.Show("Notification failed with exception " + e);
            }

			return apiResult;
	}

}

using System;
using System.Collections.Generic;
using PushSharp.Google;
using Newtonsoft.Json.Linq;

public static class Snippet
{
	public static void SendToAndroid(ref PushSharp.Google.GcmServiceBroker gcmBroker, System.String deviceID, System.String title, System.String msg)
	{
		gcmBroker.QueueNotification (new GcmNotification() {
			RegistrationIds = new List<string> { deviceID }, 
			Data = JObject.Parse("{ \"title\" : \"" + title  + "\"," + " \"body\" : \"" + msg + "\"}")
    	});
	}
}

using System;
using PushSharp;
using PushSharp.Core;
using PushSharp.Apple;
using Newtonsoft.Json.Linq;

public static class Snippet
{
	public static void SendToiOS(ref PushSharp.Apple.ApnsServiceBroker apnsBroker, System.String deviceID, System.String msg)
	{
		apnsBroker.QueueNotification (new ApnsNotification {
        	DeviceToken = deviceID,
			Payload = JObject.Parse("{\"aps\":{\"alert\":\""+msg+"\",\"badge\":\"1\"}}")
    	});
	}
}

using System;
using System.Collections.Generic;
using PushSharp.Windows;
using System.Xml.Linq;

public static class Snippet
{
	public static void SendToWin10Mobile(ref PushSharp.Windows.WnsServiceBroker wnsBroker, System.String deviceID, System.String msg)
	{
		wnsBroker.QueueNotification (new WnsToastNotification {
			ChannelUri = deviceID,
        Payload = XElement.Parse (@"
            <toast activationType='foreground' launch='"+msg+@"'>
                <visual>
                    <binding template=""ToastText01"">
                        <text id=""1"">"+msg+@"</text>
                    </binding>  
                </visual>
            </toast>")
    	});
	}
}
